const validation = (values) => {
    let errors = { };
    if (!values.email) {
      errors.email = "please Enter valid email";
    } else if (values.email.length < 15) {
      errors.email = "please Enter valid email";
    }
    if (!values.password) {
      errors.password = "Must be more than 8 characters with number letter and symbole";
    } else if (values.password.length < 9) {
      errors.password =
        "Password Required";
    }
    return errors;
  };

  export default validation;
