import React, { useState } from 'react';

const UserForm = ({ addUser, updateUser, userToEdit, cancelEdit }) => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate form inputs
    if (!name || !email || !phone) {
      alert('Please fill in all fields');
      return;
    }

    // Perform add or update based on userToEdit presence
    if (userToEdit) {
      updateUser({ id: userToEdit.id, name, email, phone });
      cancelEdit();
    } else {
      addUser({ name, email, phone });
      setName('');
      setEmail('');
      setPhone('');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="flex flex-col columns-2 lg:flex-row  bg-no-repeat bg-white mx-auto p-5">
      <input
      className='border-neutral-500'
        type="text"
        placeholder="Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        className=''
        type="tel"
        placeholder="Phone"
        value={phone}
        onChange={(e) => setPhone(e.target.value)}
      />
      <button type="submit">{userToEdit ? 'Update' : 'Add'}</button>
      {userToEdit && (
        <button type="button" onClick={cancelEdit}>
          Cancel
        </button>
      )}
    </form>
  );
};

export default UserForm;
